// Crowd level constants used across the frontend

export const CROWD_LEVELS = {
  1: "Empty",
  2: "Light",
  3: "Moderate",
  4: "Busy",
  5: "Crowded",
};

// Colors mapped to crowd levels (for charts, badges, heatmaps)
export const CROWD_COLORS = {
  1: "#4caf50", // green
  2: "#8bc34a", // light green
  3: "#ffeb3b", // yellow
  4: "#ff9800", // orange
  5: "#f44336", // red
};
